        function estadosTienda(){
            axios
                .get('https://crmcloud.acity.com.pe/webservicebeneficios/Articulo/Articulo/listararticulosxcategoria/1/0')
                .then(data => {
                    var articulos = data.data.result.result;
                    articulos.forEach((item) => {
                            if (item.stock == 0) {
                                $(".item-div-" + item.id + " span.estado").addClass("estado3");
                                $(".item-div-" + item.id + " span.estado").text("Agotado");
                                $(".item-div-" + item.id + " .add").removeClass("attToCart");
                                $(".item-div-" + item.id + " .add").removeClass("add1");
                                $(".item-div-" + item.id + " .add").addClass("add3");
                            }
                            else if (item.stock > item.stock_min) {
                                $(".item-div-" + item.id + " span.estado").addClass("d-none");
                            }
                            else if (item.stock < item.stock_min) {
                                if(item.stock > 1){
                                  $(".item-div-" + item.id + " span.estado").text("Quedan " + item.stock);
                                  $(".item-div-" + item.id + " span.estado").addClass("estado2");
                                }else{
                                  $(".item-div-" + item.id + " span.estado").text("Queda " + item.stock);
                                  $(".item-div-" + item.id + " span.estado").addClass("estado2");
                                }
                            }
                        });
                })
                .catch((err) => console.log(err))
                .finally(() => { })
        }


